sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function() {
    var userRecord = new GlideRecord("sys_user"); // sys_user is the table name we r gliding
    userRecord.get(this.getParameter('sysparm_userID')); //sysparam_userID is defined in client side we r retrieving it
    return userRecord.email + '';
  },

    type: 'GetEmailAddress'
});
